import React, { Component } from 'react'
import { connect } from 'react-redux'
import { browserHistory } from 'react-router'

const Home = require('./routes/Home/Home').default

import {
    App as AppActions,
    User as UserActions,
    Photos as PhotoActions,
    Header as HeaderActions,
} from 'actions'

import {
    Header,
} from 'components'

@connect(state => ({ header: state.Header, user: state.User, }))

/**
 * AppHeader component
 */
class AppHeader extends Component {
    /**
     * render
     * @returns markup
     */
    render() {
        const { header, user, } = this.props

        return (
            <Header
                left={header.left}
                middle={header.middle}
                right={header.right}
                userID={user.id} />
        )
    }
}

/**
 * handleRouteChange
 * @param dispatch
 * @param route
 * @param location
 */
function handleRouteChange(dispatch, route, location) {
    if (route) {

        if (route.getHeaderLeft) {
            route.getHeaderLeft(location, (err, component) => {
                dispatch(HeaderActions.left(component))
            })
        } else {
            dispatch(HeaderActions.left(null))
        }

        if (route.getHeaderMiddle) {
            route.getHeaderMiddle(location, (err, component) => {
                dispatch(HeaderActions.middle(component))
            })
        } else {
            dispatch(HeaderActions.middle(null))
        }

        if (route.getHeaderRight) {
            route.getHeaderRight(location, (err, component) => {
                dispatch(HeaderActions.right(component))
            })
        } else {
            dispatch(HeaderActions.right(null))
        }

    }
}

/**
 * App component
 * Bootstraps application
 */
class App extends Component {

    /**
     * componentDidUpdate
     * @param oldProps
     * @returns {*}
     */
    componentDidUpdate(oldProps) {

        handleRouteChange(
            this.props.dispatch,
            this.props.routes[this.props.routes.length - 1],
            this.props.location
        )

        if (oldProps.user.id != this.props.user.id && !this.props.user.id) {
            return browserHistory.push('/landing')
        }

        // user is logged in
        if (oldProps.user.id != this.props.user.id && this.props.user.id) {
            this.props.dispatch(AppActions.init())
        }

    }

    /**
     * componentDidMount
     */
    componentDidMount() {

        const { dispatch } = this.props

        FB.getLoginStatus(res => {

            if (res.status !== 'connected') {
                if (this.props.location.pathname != '/landing') {
                    browserHistory.replace('/landing')
                }
                this.props.dispatch(AppActions.initDone())
                return
            }

            dispatch(UserActions.fbLogin(res))

            if (this.props.location.pathname == '/landing') {
                browserHistory.replace('/')
            }

        })

    }

    /**
     * render
     * @returns markup
     */
    render() {

        if (this.props.loading) return <div className="loader">Loading...</div>

        if (this.props.location.pathname == '/landing') {
            return (
                <div id="root">
                    {this.props.children || <Home />}
                </div>
            )
        }

        return (
            <div id="root">
                <AppHeader />
                {this.props.children || <Home userID={this.props.user.id} />}
            </div>
        )

    }

}

export default connect(state => ({
    user: state.User,
    loading: state.App.loading,
}))(App)

import * as axios from 'axios'
import config from 'config'

/**
 * LOAD
 * @type {string}
 */
export const LOAD = 'ACTIVITY_LOAD'

/**
 * _loadRequest
 * @private
 */
export const _loadRequest = () => ({ type: LOAD, })

/**
 * _loadResponse
 * @param response
 * @private
 */
export const _loadResponse = (response) => ({ type: LOAD, response, })


/**
 * load
 * Get notifications from API for user
 * Redux Action
 * Reference: http://redux.js.org/docs/basics/Actions.html
 * @returns {Function}
 */
export function load() {
    return (dispatch, getState) => {
        return new Promise((resolve => {
            dispatch(_loadRequest())
            axios.get(`${config.api.baseUrl}/notifications?user_id=${getState().User.id}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('jwt')}`
                },
            })
            .then(res => {
                dispatch(_loadResponse(res.data))
                resolve()
            })
        }))
    }
}

import {
    Photos as PhotoActions,
    Activity as ActivityActions,
} from './'

/**
 * INIT
 * @type {string}
 */
export const INIT = 'APP_INIT'

/**
 * init
 * loads photos and activities
 * Redux Action
 * Reference: http://redux.js.org/docs/basics/Actions.html
 * @returns {Function}
 */
export function init() {
    return dispatch => {
        Promise.all([
            dispatch(PhotoActions.load()),
            dispatch(ActivityActions.load()),
        ]).then(() => {
            dispatch(initDone())
        })
    }
}

/**
 * INIT_DONE
 * @type {string}
 */
export const INIT_DONE = 'APP_INIT_DONE'

/**
 * initDone
 * @returns {{type: string}}
 */
export function initDone() {
    return {
        type: INIT_DONE
    }
}

import * as axios from 'axios'
import config from 'config'

/**
 * ADD_COMMENT
 * @type {string}
 */
export const ADD_COMMENT = 'PHOTOS_ADD_COMMENT'

/**
 * _addCommentRequest
 * @param id
 * @private
 */
export const _addCommentRequest = (id) => ({ type: ADD_COMMENT, id, })

/**
 * _addCommentResponse
 * @param id
 * @param comment
 * @param user
 * @private
 */
export const _addCommentResponse = (id, comment, user) => ({ type: ADD_COMMENT, id, comment, user,})

/**
 * addComment
 * Posts comment data to API
 * Redux Action
 * Reference: http://redux.js.org/docs/basics/Actions.html
 * @param id
 * @param text
 * @returns {Function}
 */
export function addComment(id, text) {
    return (dispatch, getState) => {
        const user = getState().User
        dispatch(_addCommentRequest(id))
        const data = {
            user_id: user.id,
            upload_id: id,
            comment: text,
        }
        axios.get(`${config.api.baseUrl}/comments`, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('jwt')}`
            },
            data: data
        })
        .then(res => {
            dispatch(_addCommentResponse(id, res.data, user))
        })
    }
}

