/**
 * Nav index component
 */
class Nav extends Component {

    /**
     * defaultProps
     * @type {{active: null, items: *[]}}
     */
    static defaultProps = {
        active: null,
        items: [
            {
                label: 'explore',
                icon: <i className="ion-map"/>,
                to: '/explore'
            },
            {
                label: 'search',
                icon: <i className="ion-search"/>,
                to: '/search'
            },
            {
                label: 'notifications',
                icon: <i className="ion-ios-bell"><i className="alert ion-record"/></i>,
                to: '/profile/notifications'
            },
        ]
    }

    /**
     * render
     * @returns markup
     */
    render() {

        let activeIndex
        this.props.items.forEach((item, i) => {
            if (item.label == this.props.active) activeIndex = i
        })

        const indicator = {
            width: (100 / this.props.items.length) + '%',
            left: (100 / this.props.items.length) * activeIndex + '%'
        }

        return (
            <div className="item middle">
                <nav>
                    {this.props.items.map((item, i) => {
                        item.active = false
                        if (item.label === this.props.active) item.active = true
                        return <Item key={`nav-item-${item.label}`} {...item} />
                    })}
                </nav>
                {this.props.active !== null
                    ? <div className="indicator" style={indicator}/>
                    : null}
            </div>
        )
    }
}

export default Nav

