export class Tab extends Component {

    /**
     * defaultProps
     * @type {{active: boolean}}
     */
    static defaultProps = {
        active: false,
    }

    /**
     * render
     * @returns markup or {null}
     */
    render() {
        return this.props.active ? this.props.children : null
    }
