import {
    Activity as ActivityActions,
    User as UserActions,
    Profile as ProfileActions,
} from 'actions'

/**
 * Activity
 * Redux Reducer for Activity action
 * Reference: http://redux.js.org/docs/basics/Reducers.html
 * @param state
 * @param action
 * @returns {*}
 * @constructor
 */
function Activity(state = [], action) {

    switch (action.type) {
        case ActivityActions.LOAD:
            if (action.response) {
                return [
                    ...action.response,
                ]
            }
            return state

        case ProfileActions.UNFOLLOW:
            return state.map((item) => {
                if (item.user_id === action.userID && item.type == 'follow') {
                    return Object.assign({}, item, {
                        following: 0,
                    })
                }
                return item
            })

        case ProfileActions.FOLLOW:
            if (action.response) {
                return state.map((item) => {
                    if (item.user_id === action.userID && item.type == 'follow') {
                        return Object.assign({}, item, {
                            following: 1,
                        })
                    }
                    return item
                })
            }
            return state
    }

    return state
}

export default Activity

