 * @param state
 * @param action
 * @returns {*}
 * @constructor
 */
function Photos(state = [], action) {

    switch (action.type) {

        case PhotoActions.PAGINATE:
            if (action.response) {
                const s = [...state]
                action.response.forEach(r => s.push(r))
                return s
            }
            return state

        case PhotoActions.LOAD:
            if (action.response) {
                return [
                    ...action.response,
                ]
            }
            return []

        case PhotoActions.LIKE:
            if (action.response) {
                return state.map(item => {

                    if (item.id == action.postID) {
                        return Object.assign({}, item, { liked: true })
                    }

                    return item
                })
            }

        case PhotoActions.UNLIKE:
            if (action.response) {
                return state.map(item => {

                    if (item.id == action.postID) {
                        return Object.assign({}, item, { liked: false })
                    }

                    return item
                })
            }


    }

    return state
}

