import {
    Search as SearchActions,
} from 'actions'


/**
 * initialState
 * @type {{active: null, recent: Array, hits: Array, term: string, total: number, results: Array}}
 */
const initialState = {
    active: null,
    recent: [],
    hits: [],
    term: '',
    total: 0,

    results: []
}

/**
 * Search
 * Redux Reducer for Search action
 * Reference: http://redux.js.org/docs/basics/Reducers.html
 * @param state
 * @param action
 * @returns {*}
 * @constructor
 */
function Search(state = initialState, action) {

    switch (action.type) {
        case SearchActions.TRIGGER:
            if (action.response) {
                return Object.assign({}, state, { active: action.search })
            }
            return state

        case SearchActions.SEARCH:
            if (action.response) {
                return Object.assign({}, state, {
                    term: action.term,
                    total: action.response.nbHits,
                    hits: [
                        ...Object.values(action.response.hits.map(a => {
                            const keys = Object.keys(a._highlightResult)
                            let type = keys[0]

                            let word;
                            switch (type) {
                                case 'hashtags':
                                    word = a._highlightResult[type].matchedWords[0]
                                break
                                case 'location':
                                    word = a.location
                                break
                                default:
                                    word = {
                                        email: a.email,
                                        name: `${a.first_name} ${a.last_name.substring()[0]}.`
                                    }
                                    //word = a.email
                                    type = 'user'
                            }

                            return {
                                word,
                                type,
                            }

                        }).filter(a => a.word).reduce((prev, a) => {

                            const word = a.word.email || a.word

                            if (prev[word + a.type]) {
                                prev[word + a.type].count++
                                return prev
                            }

                            prev[word + a.type] = {
                                word: a.word,
                                type: a.type,
                                count: 1,
                            }

                            return prev

                        }, {})),
                    ]
                })
            }
            return state

        case SearchActions.RECENT:
            if (action.response) {
                return Object.assign({}, state, {
                    recent: [...action.response],
                })
            }
            return state

        case SearchActions.RESULTS:
            if (action.response) {
                return Object.assign({}, state, {
                    results: [...action.response]
                })
            }
            return state

    }

    return state
}

export default Search

import {
    Stats as StatsActions,
} from 'actions'

/**
 * iniitalState
 * @type {{profileViews: {count: number, increment: number, color: string}, itemViews: {count: number, increment: number, color: string}, following: {count: number, increment: string, color: string}, followers: {count: number, increment: string, color: string}, mostViewed: Array, geoViews: Array}}
 */
const initialState = {
    profileViews: {
        'count': 0,
        'increment': 24,
        'color': 'green'
    },
    itemViews: {
        'count': 0,
        'increment': 24,
        'color':
        'green'
    },
    following: {
        'count': 0,
        'increment': '',
        'color': ''
    },
    followers: {
        'count': 0,
        'increment': '',
        'color': ''
    },
    mostViewed: [

    ],
    geoViews: []
}

/**
 * Stats
 * Redux Reducer for Stats action
 * Reference: http://redux.js.org/docs/basics/Reducers.html
 * @param state
 * @param action
 * @returns {*}
 * @constructor
 */
function Stats(state = initialState, action) {
    switch (action.type) {

        case StatsActions.LOAD:
            if (action.response) {
                if (action.response.itemViews >= 0) {
                  var newState = Object.assign({}, state, {
                      itemViews: {'count': action.response.itemViews},
                  })
                } else if (action.response.profileViews >= 0) {
                  var newState = Object.assign({}, state, {
                      profileViews: {'count': action.response.profileViews},
                  })
                } else if (action.response.mostViewed) {
                  var newState = Object.assign({}, state, {
                      mostViewed: action.response.mostViewed,
                  })
                } else if (action.response.geoViews) {
                  var newState = Object.assign({}, state, {
                      geoViews: action.response.geoViews,
                  })
                } else if (action.response.newFollowers) {
                  var newState = Object.assign({}, state)
                  var increment = action.response.newFollowers.result
                  newState['followers']['increment'] = increment
                  newState['followers']['color'] = (increment) ? 'green' : 'red'
                }

                return newState
            }

            return initialState

    }

    return state

}

