/**
 * FollowingActivity
 * '/profile/:id/following-activity'
 * React Route - Documentation: https://github.com/reactjs/react-router/tree/master/docs
 */
class FollowingActivity extends Component {

    /**
     * handleFollowBack
     * @param e
     * @param user
     */
    handleFollowBack = (e, user) => {
        this.props.dispatch(ProfileActions.follow(user.id))
    }

    /**
     * handleUnfollow
     * @param e
     * @param user
     */
    handleUnfollow = (e, user) => {
        this.props.dispatch(ProfileActions.unfollow(user.id))
    }

    /**
     * render
     * @returns markup
     */
    render() {
        return (
            <div className="page">
                <div className="following-activity">
                    <ul className="timeline">

                        {this.props.activity.map(item => {

                            const {
                                email,
                                first_name,
                                last_id,
                                last_name,
                                timestamp,
                                type,
                                user_id,
                                ...data,
                                } = item

                            let child

                            switch (type) {
                                case 'follow':
                                    child = (
                                        <Activity.Following
                                            onFollowBack={this.handleFollowBack}
                                            onUnfollow={this.handleUnfollow}
                                            following={data.following === 1}
                                            user={{
                                                firstName: this.props.user.first_name,
                                                lastName: this.props.user.last_name.substr(0)+'.',
                                                id: this.props.user.first_name,
                                                email: this.props.user.email,
                                            }}/>
                                    )
                                    break
                                case 'like':
                                    child = <Activity.Liked pictures={[{ src: data.filename, id:  data.upload_id, }]}/>
                                    break

                                default:
                                    return
                            }

                            return (
                                <Activity.Item
                                    key={`activity-item-${last_id}`}
                                    type={type}
                                    timestamp={timestamp}
                                    actor={{
                                        firstName: first_name,
                                        lastName: last_name.substr(0) + '.',
                                        id: user_id,
                                        email: email,
                                    }}>
                                    {child}
                                </Activity.Item>
                            )
                        })}
                    </ul>
                </div>
            </div>
        )
    }

}

