import React, { Component } from 'react'
import { findDOMNode } from 'react-dom'
import { connect } from 'react-redux'
import { Link } from 'react-router'
import Ink from 'react-ink'
import stream from 'getstream'
import config from 'config'

import {
    PhotoList,
} from 'components'

import {
    User as UserActions,
    Photos as PhotosActions,
} from 'actions'

/**
 * Landing
 * Landing component used by Home route
 */
class Landing extends Component {

    /**
     * defaultProps
     * @type {{onFetch: Landing.defaultProps.onFetch}}
     */
    static defaultProps = {
        onFetch: () => {
        },
    }

    /**
     * componentWillMount
     */
    componentWillMount() {

        // instantiate a new client (client side)
        this.client = stream.connect(config.stream.key, null, config.stream.appId)

        // follow 'timeline_flat' feed
        this.timeline = this.client.feed('timeline_flat', this.props.userID, this.props.tokens.timelineFlat)

    }

    /**
     * handleScroll
     * @param e event
     */
    handleScroll = (e) => {

        if (this.$scroll) clearTimeout(this.$scroll)

        this.$scroll = setTimeout(() => {

            const d = findDOMNode(this._pageElm)
            const threshold = (d.offsetHeight / 2)

            if ((d.scrollTop + d.offsetHeight) >= (d.scrollHeight - threshold)) {
                this.props.onFetch()
            }

        }, 25)
    }

    /**
     * componentDidMount
     */
    componentDidMount() {
        findDOMNode(this._pageElm).addEventListener('scroll', this.handleScroll)
    }

    /**
     * componentWillUnmount
     */
    componentWillUnmount() {
        findDOMNode(this._pageElm).removeEventListener('scroll', this.handleScroll)
    }


    /**
     * handleRefresh
     * @param e event
     */
    handleRefresh = (e) => {
        window.location.reload()
    }

    /**
     *
     * @returns markup
     */
    renderListOrMessage = () => {

        if (!this.props.photos.length) {
            return (
                <div className="empty-message">
                    <div>Check out the <Link to="/explore">explore page</Link> to get started!</div>
                </div>
            )
        }

        return (
            <div>
                <PhotoList onLike={this.props.onLike} photos={this.props.photos}/>
                <button className="load-posts" onClick={this.handleRefresh}>
                    Load New Posts
                    <Ink />
                </button>
            </div>
        )

    }

    /**
     *
     * @returns markup
     */
    render() {
        return (
            <div className="page full-page" ref={c => this._pageElm = c}>
                {this.renderListOrMessage()}
                <Link to={`/upload`}>
                    <div className="add-photo">
                        <i className="ion-plus"/>
                        <Ink />
                    </div>
                </Link>
            </div>
        )
    }
}

