import React, { Component } from 'react'
import { browserHistory } from 'react-router'
import { connect } from 'react-redux'
import * as axios from 'axios'
import config from 'config'
import Ink from 'react-ink'

import {
    Photo as PhotoActions,
    Photos as PhotosActions,
} from 'actions'

class Upload extends Component {

    /**
     * state
     * @type {{caption: string, hashtags: string, location: string, filename: string, uploadState: string, disabledUploadState: boolean}}
     */
    state = {
        caption: '',
        hashtags: '',
        location: '',
        filename: 'Select Image',
        uploadState: 'Upload',
        disabledUploadState: false,
    }

    /**
     * handleFileClick
     */
    handleFileClick = () => {
        document.getElementById('file').click()
    }

    /**
     * handleCaptionChange
     * @param e event
     */
    handleCaptionChange = (e) => this.setState({
        caption: e.target.value,
    })

    /**
     * handleLocationChange
     * @param e event
     */
    handleLocationChange = (e) => this.setState({
        location: e.target.value,
    })

    /**
     * handleHashtagsChange
     * @param e event
     */
    handleHashtagsChange = (e) => this.setState({
        hashtags: e.target.value,
    })

    /**
     * handleSubmit
     * @param e event
     */
    handleSubmit = (e) => {

        e.preventDefault()

        let form = document.getElementById('upload'),
            file = document.getElementById('file').files[0],
            data = new FormData()

        data.append('image', file, file.name)

        data.append('user_id', this.props.user.id)
        data.append('caption', this.state.caption)
        data.append('hashtags', this.state.hashtags)
        data.append('location', this.state.location)

        this.setState({ uploadState: 'Uploading...' })
        this.setState({ disabledUploadState: true })

        axios.post(`${config.api.baseUrl}/uploads`, data, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('jwt')}`
            },
        })
            .then(res => {
                this.props.dispatch(PhotosActions.add(res.data))
                this.setState({ uploadState: 'Success!' })
                browserHistory.push('/photos/' + res.data.id)
            })
            .catch(res => {
                this.setState({
                    uploadState: 'Error! Please click to try again.',
                    disabledUploadState: false,
                })
            })

    }

    /**
     * handleChange
     * @param e event
     */
    handleChange = (e) => {

        e.preventDefault()

        let reader = new FileReader(),
            file   = e.target.files[0]

        this.setState({ filename: file.name })

    }

    /**
     * render
     * @returns markup
     */
    render() {
        return (
            <div className="page">
                <form id="upload" className="upload-form" onSubmit={(e) => this.handleSubmit(e)}>
                    <div className="form-group upload" onClick={this.handleFileClick}>
                        <label for="caption">
                            <i className="ion-ios-cloud-upload-outline"></i>
                            <span id="filename">{this.state.filename}</span>
                        </label>
                        <input id="file" type="file" name="file" accept="image/*" required onChange={(e)=>this.handleChange(e)} />
                    </div>
                    <div className="inner">
                        <div className="form-group">
                            <label for="caption">Caption</label><br />
                            <input type="text" placeholder="My treehouse" name="caption" maxLength="140" value={this.state.caption} required onChange={this.handleCaptionChange} />
                        </div>
                        <div className="form-group">
                            <label for="location">Location</label><br />
                            <input type="text" placeholder="City, State OR Country" name="location" value={this.state.location} required onChange={this.handleLocationChange} />
                        </div>
                        <div className="form-group">
                            <label for="hashtags">Tags</label><br />
                            <input type="text" placeholder="#cabin" name="hashtags" maxLength="140" value={this.state.hashtags} required onChange={this.handleHashtagsChange} />
                        </div>
                        <div className="form-group">
                            <button type="submit" id="upload-btn" disabled={this.state.disabledUploadState}>
                                {this.state.uploadState}
                                <Ink />
                            </button>
                        </div>
                    </div>
                </form>
            </div>
        )
    }

}

/**
 * connect
 * Connects React component to a Redux store
 * Documentation: https://github.com/reactjs/react-redux/blob/master/docs/api.md#connectmapstatetoprops-mapdispatchtoprops-mergeprops-options
 */
export default connect(state => ({ user: state.User }))(Upload)

module.exports = {
    path: '/upload',

    /**
     * getComponent
     * @param location
     * @param cb {Function} callback
     */
    getComponent(location, cb) {
        cb(null, require('./Upload').default)
    },
}

/**
 * viewPhoto
 * sends Keen tracking event for photo view
 * @param userId
 * @param postId
 * @param postAuthorId
 */
export const viewPhoto = (userId, postId, postAuthorId) => {
    let eventDetails = {
        user: userId,
        postId: postId,
        postAuthorId: postAuthorId,
        type: 'item',
    }
    _trackKeenEvent(eventDetails, 'views')
}

/**
 * viewProfile
 * sends Keen tracking event for profile view
 * @param userId
 * @param profileUserId
 */
export const viewProfile = (userId, profileUserId) => {
    let eventDetails = {
        user: userId,
        profileUser: profileUserId,
        type: 'user',
    }
    _trackKeenEvent(eventDetails, 'views')
}

/**
 * likePhoto
 * sends Keen tracking event for photo like
 * @param userId
 * @param postId
 */
export const likePhoto = (userId, postId) => {
    let eventDetails = {
        user: userId,
        postId: postId
    }
    _trackKeenEvent(eventDetails, 'like')
}

/**
 * followUser
 * sends Keen tracking event for user follow
 * @param userId
 * @param targetId
 * @param direction
 */
export const followUser = (userId, targetId, direction) => {
    let eventDetails = {
        user: userId,
        targetId: targetId,
        direction: direction,
        directionInt: (direction=='follow') ? 1 : -1
    }
    _trackKeenEvent(eventDetails, 'follow')
}

/**
 * _trackKeenEvent
 * sends Keen tracking event
 * @param eventObject
 * @param collectionName
 * @private
 */
const _trackKeenEvent = (eventObject, collectionName) => {
    var viewEventDefaults = {
        ip_address: "${keen.ip}",
        user_agent: "${keen.user_agent}",
        keen: {
            timestamp: new Date().toISOString(),
            addons: [{
                name: "keen:ip_to_geo",
                input: {
                    ip: "ip_address"
                },
                output: "ip_geo_info"
            }, {
                name: "keen:ua_parser",
                input: {
                    ua_string: "user_agent"
                },
                output: "parsed_user_agent"
            }]
        }
    }
    let merged = Object.assign(viewEventDefaults, eventObject)
    keenClient.addEvent(collectionName, merged, function(err, res) {
        if (err) {
            console.log(err)
            return;
        }
        //console.log(res)
    })
}
